// SPDX-License-Identifier: CC-BY-NC-ND-4.0
pragma solidity ^0.8.9;

abstract contract LilVillainsArk {
  uint256 private _basePaymentAmount;
  address private _arkAddress;

  constructor(address arkAddress_) {
    _arkAddress = arkAddress_;
  }

  function _setArkAddress(address arkAddress) internal {
    _arkAddress = arkAddress;
  }

  function getArkAddress() public view returns (address) {
    return _arkAddress;
  }

  function _setBasePayment(uint256 basePayment) internal {
    _basePaymentAmount = basePayment;
  }

  function _getBasePaymentByAmount(uint256 amount) internal view returns (uint256 paymentAmount) {
    uint256 multiplier;

    if (amount <= 5) multiplier = 0;
    else if (amount <= 10) multiplier = 1;
    else if (amount <= 15) multiplier = 2;
    else if (amount <= 20) multiplier = 3;
    else if (amount <= 30) multiplier = 4;
    else multiplier = 5;

    paymentAmount = _basePaymentAmount + ((_basePaymentAmount / 2) * multiplier);
  }

  function _collectToArk(uint256 amount) internal {
    payable(_arkAddress).transfer(amount);
  }
}
